import React from 'react';
import axios from 'axios';
// import Wobble from './Wobble';

import './App.css';
import './Wobble.css';

class App extends React.Component {
  state = {
    advice: '',
  }

  componentDidMount() {
    this.fetchAdvice();
  }

  fetchAdvice = () => {
    axios.get('https://api.adviceslip.com/advice')
      .then((response) => {
        const { advice } = response.data.slip;

        this.setState({ advice });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <canvas>
        <p class="codepen" data-height="300" data-default-tab="html,result" data-slug-hash="yLNaLMw" data-user="neave" style="height: 300px; box-sizing: border-box; display: flex; align-items: center; justify-content: center; border: 2px solid; margin: 1em 0; padding: 1em;">
  <span>See the Pen <a href="https://codepen.io/neave/pen/yLNaLMw">
  Wobble</a> by Paul Neave (<a href="https://codepen.io/neave">@neave</a>)
  on <a href="https://codepen.io">CodePen</a>.</span>
</p>
<script async src="https://cpwebassets.codepen.io/assets/embed/ei.js"></script>
